    // Slayer's Prey

    // make sure we have a selected token and target 
    if (args[0].hitTargets.length === 0) return;
    let target = args[0].hitTargets[0]._id;
    let actorId = args[0].actor._id; // actor who cast the spell
    actor = game.actors.get(actorId);
    if (!actor || !target) {
      console.error("Slayer's Prey: no token/target selected");
      return;
    }

    // First remove the old effects from the target and ourselves 
    let item = actor.items.get(args[0].id);

    // Remove the effect from old target if we are changing targets
    let oldTarget = getProperty(actor.data, "flags.midi-qol.slayersPrey");
    DAE.deleteActiveEffect(oldTarget, item.uuid);

    // Now remove it from ourselves since we applied it by hand in the first place
    let effect = actor.effects.find(e=>e.data.origin===item.uuid);
    if (effect) await actor.deleteEmbeddedEntity("ActiveEffect", effect.id);

    // Now create an active effect on ourselves
    //  one change showing the hunter's mark icon on the caster
    //  the second setting the flag for the macro to be called when damaging an opponent
    const effectData = {
      changes: [
        {key: "flags.midi-qol.slayersPrey", mode: CONST.ACTIVE_EFFECT_MODES.OVERRIDE, value: target, priority: 20}, // who is marked, mode 5 to override any previous setting
        {key: "flags.dnd5e.DamageBonusMacro", mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM, value: `ItemMacro.${args[0].item.name}`, priority: 20} // macro to apply the damage, mode 0 for comma separated list since there may be other effects doing the same 
      ], 
      origin: args[0].uuid, //flag the effect as associated to the spell being cast
      disabled: false,
      duration: args[0].item.effects[0].duration,
      icon: args[0].item.img,
      label: args[0].item.name
    };
    await actor.createEmbeddedEntity("ActiveEffect", effectData);
} else if (args[0].tag === "DamageBonus") {

    // make sure someone was hit (if damage always rolled, may not be the case)
    if (args[0].hitTargets.length === 0) return;
    let targetId = args[0].hitTargets[0]._id;

    // only weapon attacks
    if (!["mwak","rwak"].includes(args[0].item.data.actionType)) return {};

    // only on the marked target
    if (targetId !== getProperty(args[0].actor.flags, "midi-qol.slayersPrey")) return {};

    // check to make sure this is the first hit this turn
    // if so, going to do extra damage now, so update the time flag 
    if (game.combat) {
      let combatTime = game.combat.round + game.combat.turn /100;
      let lastTime = getProperty(token.data.flags, "midi-qol.slayersPreyTime");
      if (combatTime === lastTime) {
        MidiQOL.warn("Slayer's Prey Damage: Already done Slayer's Prey damage this turn");
        return {};
      } else {
        // we're going to do extra damage now, so update the time flag to now 
        setProperty(token.data.flags, "midi-qol.slayersPreyTime", combatTime)
      }
    }

    let damageType = args[0].item.data.damage.parts[0][1];
    const diceMult = args[0].isCritical ? 2: 1;
    return {damageRoll: `${diceMult}d6[${damageType}]`, flavor: "Slayer's Prey Damage"}
}","author":"bM8qxruK83CQDvp1"},"data":{"name":"Slayer's Prey","type":"script","scope":"global","command":"if (args[0].tag === "OnUse") {

