    if (game.combat) {
      let combatTime = game.combat.round + game.combat.turn /100;
      let lastTime = getProperty(token.data.flags, "midi-qol.slayersPreyTime");
      if (combatTime === lastTime) {
        MidiQOL.warn("Slayer's Prey Damage: Already done Slayer's Prey damage this turn");
        return {};
